vertexshader.source:
uniform mat4 u_modelViewProjectionMatrix;

attribute vec4 a_position;
attribute vec4 a_color;
varying vec4 color;

void main(){
  gl_Position = u_modelViewProjectionMatrix * a_position;
  color = a_color;
}

fragmentshader.source:
varying vec4 color;

void main(){
  gl_FragColor = color;
}
